import React, { useEffect, useRef, useState } from "react";
import Handsontable from "handsontable";
import { HotTable, HotColumn } from "@handsontable/react";
import "handsontable/dist/handsontable.min.css";
import "./App.css";

const tableTitle = {
  "Core People Data": ["Employee Unique ID", "First Name", "Last Name", "Email", "Company", "Legal Entity", "Employee Status", "Employee Type", "Date of Hire", "Immigration Supported", "Office/\nLocation", "Remote working", "Current Job Level", "Current Job Title", "Current Job Family Group", "Function"],
  "M&A Integration Process": ["Uber EEID", "In Deal (Y/N)", "WAVE", "Disposition", "Cohort", "Reason left Deal", "Date Left"],
  "Legal": ["Gender", "Date of Birth", "Race/Ethnicity", "Nationality", "Supervisor Unique ID", "Supervisor Organization", "Supervisor Email", "AIA Flag", "Legal Hold", "Garden Leave", "Employee Transfer Decision", "Talent Release Justification", "Notification Date", "Last Day Worked", "Termination Date", "Office location", "Disposition"],
  "Immigration": ["Immigration Supported", "Immigration Status", "Visa Expiration", "Can start on Day 1?", "Eligible for Immi Garden Leave"],
  "Mobility": ["On Assignment (Y/N)", "Assignment end date", "Relocation (Y/N)"],
  "Comp (PM)": ["Previous Base Salary", "Previous Total Cash", "Previous Equity holdings", "Vesting Schedule", "Shares per Vest Cycle", "Uber Location", "Uber Job Ladder", "Uber Level", "Uber Base", "Uber Bonus", "Uber Equity"],
  "Comp (M&A)": ["???Here"],
  "TA": [null],
  "Welcome / POps": ["Email Address", "Name", "Personal Email", "EEID", "Job Title", "Manager", "Uber Office Location", "Base Salary", "Bonus Target", "New Hire Grant", "Bridge Bonus", "Retention Bonus"],
  "Separation / POps": ["Email Address", "Name", "Personal Email", "EEID", "Disposition", "Name", "Personal Email", "Original Start Date", "Tenured Uber \n(Years + Quarters)", "Current Unvested Equity", "Weekly Salary", "2 weeks pay", "Base Severance\n( 4 weeks) ", "Tenure Severance", "Uber Severance\n( Base + Tenure)", "Uber Severance less Equity", "Is equity greater than Uber severance? (greater/ Less)", "Final Severance amount", "COBRA amount", "6 Month Benefits Cash Payment", "LOA Flag?", "Immigration Check", "Severance template", "Hourly Rate", "Hours per Week", "Integration Bonus", "Location", "CA or Non CA", "Date of Birth", "Age", "Job Title", "Uber Severance Policy", "WARN check"],
}

const userGroup = {
  "admin": null,
  "user1": ["M&A Integration Process", "Legal", "Immigration"],
  "user2": ["M&A Integration Process", "Legal", "Immigration", "Mobility", "Comp (PM)", "Comp (M&A)", "TA", "Welcome / POps"],
  "user3": ["M&A Integration Process", "Legal", "Immigration", "Mobility", "Comp (PM)", "Comp (M&A)", "TA", "Separation / POps"]
}

const nestedHeaders = [
  // get overall title
  Object.keys(tableTitle).map((key) => {
    return {
      label: key,
      colspan: tableTitle[key].length
    };
  }),
  // get all sub titles
  Object.values(tableTitle).reduce((previous, value) => {
    return previous.concat(value);
  }, [])
];

const demoData = Handsontable.helper.createSpreadsheetData(50, nestedHeaders[1].length);


function App() {

  const hotTable = useRef();
  const [activeUser, setActiveUser] = useState('admin');

  useEffect(() => {
    // collapse all titles
    if (hotTable.current) {
      hotTable.current.hotInstance.getPlugin("collapsibleColumns").collapseAll();
    }
  });

  const hideColumn = (userType) => {
    if (!hotTable.current) return;
    // reset the table in to default condition
    let prevHiddenColumn = hotTable.current.hotInstance.getPlugin("hiddenColumns").getHiddenColumns();
    hotTable.current.hotInstance.getPlugin("hiddenColumns").showColumns(prevHiddenColumn);
    // hide column based on its corresponding userType
    hotTable.current.hotInstance.getPlugin("hiddenColumns").hideColumns([...Array(16).keys()]);
    hotTable.current.hotInstance.render();
  };

  return (
    <div className="app-container">
      <h2 className="app-title">M&A Data Demo</h2>
      <div className="user-role-group">
        <p>click below buttons to switch between different user types</p>
        {
          Object.keys(userGroup).map((key) => {
            return <div className={`app-button ${key === activeUser ? "active" : ""}`} key={key} onClick={() => hideColumn({key})}>{key}</div>;
          })
        }
        {/* <div className="app-button active" onClick={() => hideColumn("admin")}>Admin</div>
        <div className="app-button" onClick={() => hideColumn("user1")}>User1</div>
        <div className="app-button" onClick={() => hideColumn("user2")}>User2</div>
        <div className="app-button" onClick={() => hideColumn("user3")}>User3</div> */}
        <p className="current-user">Current User is {"Admin"}</p>
      </div>
      <HotTable
        id="app-table"
        ref={hotTable}
        data={demoData}
        height={500}
        width={"100%"}
        colWidths={200}
        search={true}
        nestedHeaders={nestedHeaders}
        collapsibleColumns={true}
        dropdownMenu={true}
        multiColumnSorting={true}
        filters={true}
        rowHeaders={true}
        manualColumnResize={true}
        manualRowResize={true}
        manualRowMove={true}
        hiddenColumns={true}
        licenseKey="non-commercial-and-evaluation">
      </HotTable>
      <div className="table-column-group">
        <p>click below buttons to hide certain column</p>
        {
            Object.keys(tableTitle).map((key) => {
              return <div className="app-button" key={key} onClick={() => hideColumn("admin")}>{key}</div>;
            })
        }
      </div>
    </div>
  );
}

export default App;
