let hiddenCategoryArr = [];

function App() {

  const hotTable = useRef();
  const [hiddenColumnArr, setHiddenColumnArr] = useState([]);
  const [activeUser, setActiveUser] = useState('admin');



  useEffect(() => {
    // collapse all titles
    if (hotTable.current) {
      hotTable.current.hotInstance.getPlugin("collapsibleColumns").collapseAll();

      hotTable.current.hotInstance.getPlugin("hiddenColumns").hideColumns(hiddenColumnArr);
      hotTable.current.hotInstance.render();
    }
  });

  const testAfterChange = (changes) => {
    if (!changes) return;
    let [row, col, preValue, afterValue] = changes[0];
    if (preValue === afterValue) return;
    alert(`value at row: ${row}, col: ${col} has changed from ${preValue} to ${afterValue}`);
  }

  const resetTable = () => {
    // reset the table in to default condition
    let prevHiddenColumn = hotTable.current.hotInstance.getPlugin("hiddenColumns").getHiddenColumns();
    hotTable.current.hotInstance.getPlugin("hiddenColumns").showColumns(prevHiddenColumn);
  }

  const convertCategoryToColumn = () => {
    if (!hiddenCategoryArr) {
      setHiddenColumnArr([]);
      return;
    }
    let newHiddenColumn = [];
    let index = 0;
    Object.entries(tableTitle).forEach(([key, value], _) => {
      if (hiddenCategoryArr.includes(key)) {
        newHiddenColumn = newHiddenColumn.concat(Array.from({ length: value.length }, (_, i) => i + index));
      }
      index += value.length;
    });
    setHiddenColumnArr(newHiddenColumn);
  }

  const hideColumnBasedOnUser = (userType) => {
    // change active user
    setActiveUser(userType);

    // hide column based on its corresponding userType
    hiddenCategoryArr = [];
    hideColumnBasedOnSelect(userGroup[userType]);
  };

  const hideColumnBasedOnSelect = (titles, individualFlag = false) => {
    if (!hotTable.current) return;
    resetTable();

    if (individualFlag) {
      let title = titles[0];
      let index = Object.keys(tableTitle).indexOf(title);
      let currentElement = document.getElementById(`title-${index}`);
      if (hiddenCategoryArr.includes(title)) {
        const tempIndex = hiddenCategoryArr.indexOf(title);
        if (tempIndex > -1) {
          hiddenCategoryArr.splice(tempIndex, 1);
          currentElement.classList.add('active');
        }
      } else {
        hiddenCategoryArr.push(title);
        currentElement.classList.remove('active');
      }
    } else {
      Object.keys(tableTitle).forEach((title, index) => {
        let currentElement = document.getElementById(`title-${index}`);
        if (titles.includes(title)) {
          if (hiddenCategoryArr.includes(title)) {
            const tempIndex = hiddenCategoryArr.indexOf(title);
            if (tempIndex > -1) {
              hiddenCategoryArr.splice(tempIndex, 1);
              currentElement.classList.add('active');
            }
          } else {
            hiddenCategoryArr.push(title);
            currentElement.classList.remove('active');
          }
        } else {
          currentElement.classList.add('active');
        }
      });
    }
    convertCategoryToColumn();
  }

  return (
    <div className="app-container">
      <h2 className="app-title">M&A Data Demo</h2>
      <div className="user-role-group">
        <p>click below buttons to switch between different user types</p>
        {
          Object.keys(userGroup).map((key) => {
            return <div className={`app-button ${key === activeUser ? "active" : ""}`} key={key} onClick={() => hideColumnBasedOnUser(key)}>{key}</div>;
          })
        }
        <p className="current-user">Current User is {activeUser}</p>
      </div>
      <div className="table-column-group">
        <p>click below buttons to hide certain column</p>
        {
          Object.keys(tableTitle).map((key, index) => {
            return <div className="app-button active" id={`title-${index}`} key={key} onClick={() => hideColumnBasedOnSelect([key], true)}>{key}</div>;
          })
        }
      </div>
      <HotTable
        id="app-table"
        ref={hotTable}
        data={cleanData}
        height={500}
        width={"100%"}
        colWidths={250}
        search={true}
        nestedHeaders={nestedHeaders}
        collapsibleColumns={true}
        dropdownMenu={true}
        multiColumnSorting={true}
        filters={true}
        rowHeaders={true}
        fixedColumnsLeft={1}
        manualColumnResize={true}
        manualRowResize={true}
        manualRowMove={true}
        hiddenColumns={true}
        afterChange={(changes) => testAfterChange(changes)}
        licenseKey="non-commercial-and-evaluation">

        {/* <HotColumn data={1} readOnly={true} /> */}

      </HotTable>
    </div>
  );
}

export default App;
